mapper.update:
UPDATE board_article
SET
title=#{title}
, writer=#{writer}
, content=#{content}
WHERE articleid=#{articleid}
AND
isdeleted=0

UPDATE board_comment
SET content=#{content}
WHERE commentid=#{commentid}


UPDATE board_comment
   SET isdeleted=1
 WHERE commentid=#{commentid}

mapper.select:
SELECT id, name, isavailable, iscommentable, readonly
  FROM board_group
 WHERE isdeleted=0 AND id=#{id}
