mapper.update:
UPDATE board_article
SET
title=#{title}
, writer=#{writer}
, content=#{content}
WHERE articleid=#{articleid}
AND
isdeleted=0

UPDATE TBL_BOARDREPLY
SET REMEMO=#{rememo}
WHERE RENO=#{reno}

mapper.select:
SELECT id, name, isavailable, iscommentable, readonly
  FROM board_group
 WHERE isdeleted=0 AND id=#{id}
